    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
         String nombresso= System.getProperty("os.name");
         this.NombreSSO.setText(nombresso);
         
         
         String versionsso= System.getProperty("os.version");
         this.version.setText(versionsso);
         
         String versionjre= System.getProperty("java.vendor");
         this.jre.setText(versionjre);
         
        String versionjre2= System.getProperty("java.version");
         this.versionjre.setText(versionjre2);
        
    }//GEN-LAST:event_jButton1ActionPerformed

