    private void BotonSaludoMouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BotonSaludoMouseReleased
        switch (CampoNombre.getText())
        {
            case "":
                CampoSaludo.setText("Hola, desconocido. Mejor dime tu nombre");
                break;
            case "yo":
                CampoSaludo.setText("Hola, Tu");
            case "tu":
                CampoSaludo.setText("Hola, Yo");
                break;
            default:
                CampoSaludo.setText("Hola, " + CampoNombre.getText());
        }
        
    }//GEN-LAST:event_BotonSaludoMouseReleased

