    private void BotonDolarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonDolarActionPerformed
        // TODO add your handling code here:
      float euro=Float.parseFloat(TextEuro.getText());
        euro=(float) (euro/1.15);
        TextDolar.setText(Float.toString(euro));       
        
    }//GEN-LAST:event_BotonDolarActionPerformed

    private void BotonEuroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonEuroActionPerformed
        float numero=Float.parseFloat(TextDolar.getText());        
        numero=(float) (numero*1.15);
        TextEuro.setText(Float.toString(numero));
    }//GEN-LAST:event_BotonEuroActionPerformed

