    private void CalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcularActionPerformed
          
          NombreSO.setText(System.getProperty("os.name"));
          VersionSO.setText(System.getProperty("os.version"));
          NombreJre.setText(System.getProperty("java.vendor"));
          VersionJre.setText(System.getProperty("java.version"));
           
    }//GEN-LAST:event_CalcularActionPerformed

