    private void btDolarsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btDolarsActionPerformed
        float euros=Float.parseFloat(txfEuros.getText());
        txfDolars.setText(Double.toString(euros*cambio));
    }//GEN-LAST:event_btDolarsActionPerformed

    private void btEurosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btEurosActionPerformed
        float dolars=Float.parseFloat(txfDolars.getText());
        txfEuros.setText(Double.toString(dolars/cambio));
    }//GEN-LAST:event_btEurosActionPerformed

